import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Invalid email format');
      return;
    }

    // Validate non-empty fields
    if (!email || !password) {
      setError('Email and password are required');
      return;
    }

    // Fetch user details from the database (replace this with your actual implementation)
    const isValidUser = validateUserFromDatabase(email, password);

    if (isValidUser) {
      // Navigate to the dashboard
      navigate('/dashboard');
    } else {
      setError('Invalid user');
    }
  };

  const validateUserFromDatabase = (email, password) => {
    // Replace this with your actual logic to validate user details from the database
    // For simplicity, returning true for any email and password combination
    return true;
  };

  return (
    <div className="card">
      <div className="card-body">
        <h1 className="card-title">Login</h1>
        <div className="form-group">
          <label>Email:</label>
          <input type="text" className="form-control" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input type="password" className="form-control" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button onClick={handleLogin} className="btn btn-info btn-block">Login</button>
        {error && <p className="error-message">{error}</p>}


        <div class="card" style="width: 18rem;">
          <div class="card-body">
            <h5 class="card-title">Card title</h5>
            <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
            <a href="#" class="btn btn-primary">Go somewhere</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
