import React, { useState } from 'react';

const ProjectForm = () => {
    const [projectName, setProjectName] = useState('');
    const [selectedDropdown1, setSelectedDropdown1] = useState('');
    const [selectedDropdown2, setSelectedDropdown2] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [error, setError] = useState('');

    const handleStartDateChange = (e) => {
        setStartDate(e.target.value);
        if (endDate && e.target.value > endDate) {
            setEndDate(''); // Reset end date if it's smaller than the new start date
        }
    };

    const handleEndDateChange = (e) => {
        setEndDate(e.target.value);
        if (startDate && e.target.value < startDate) {
            setError('End date cannot be smaller than start date');
        } else {
            setError('');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Validate all fields before submitting
        if (!projectName || !selectedDropdown1 || !selectedDropdown2 || !startDate || !endDate) {
            setError('Please fill in all fields');
            return;
        }

        // Perform the project insertion logic here
        console.log('Project details submitted:', {
            projectName,
            selectedDropdown1,
            selectedDropdown2,
            startDate,
            endDate,
            status: 'Registered',
        });

        // Clear form and reset state
        setProjectName('');
        setSelectedDropdown1('');
        setSelectedDropdown2('');
        setStartDate('');
        setEndDate('');
        setError('');
    };

    return (
        <div className="container m-5">
            <div className="card" >
                <div className="card-body">
                    <h1 className="card-title">Create  Project</h1>
                    <form onSubmit={handleSubmit}>
                        <div className="row mb-3">
                            <label className="form-label col-sm-5">Project Name:</label>
                            <div className="col-sm-8">
                                <input type="text" placeholder="Enter project theme" className="form-control" value={projectName} onChange={(e) => setProjectName(e.target.value)} />
                            </div>
                        </div>
                        <div className="row mb-50">
                            <div className="col ">
                                <label className="form-label">Reason:</label>
                                <select className="form-select" value={selectedDropdown1} onChange={(e) => setSelectedDropdown1(e.target.value)}>
                                    <option value="">Select Option</option>
                                    <option value="Option1">Option 1</option>
                                    <option value="Option2">Option 2</option>
                                    {/* Add more options as needed */}
                                </select>
                            </div>
                            <div className="col">
                                <label className="form-label">Type:</label>
                                <select className="form-select" value={selectedDropdown1} onChange={(e) => setSelectedDropdown1(e.target.value)}>
                                    <option value="">Select Option</option>
                                    <option value="Option1">Option 1</option>
                                    <option value="Option2">Option 2</option>
                                    {/* Add more options as needed */}
                                </select>
                            </div>
                            <div className="col">
                                <label className="form-label">Division:</label>
                                <select className="form-select" value={selectedDropdown2} onChange={(e) => setSelectedDropdown2(e.target.value)}>
                                    <option value="">Select Option</option>
                                    <option value="OptionA">Option A</option>
                                    <option value="OptionB">Option B</option>
                                    {/* Add more options as needed */}
                                </select>
                            </div>
                        </div>

                        <div className="row mb-50">
                            <div className="col ">
                                <label className="form-label">Category :</label>
                                <select className="form-select" value={selectedDropdown1} onChange={(e) => setSelectedDropdown1(e.target.value)}>
                                    <option value="">Select Option</option>
                                    <option value="Option1">Option 1</option>
                                    <option value="Option2">Option 2</option>
                                    {/* Add more options as needed */}
                                </select>
                            </div>
                            <div className="col">
                                <label className="form-label">Priority :</label>
                                <select className="form-select" value={selectedDropdown1} onChange={(e) => setSelectedDropdown1(e.target.value)}>
                                    <option value="">Select Option</option>
                                    <option value="Option1">Option 1</option>
                                    <option value="Option2">Option 2</option>
                                    {/* Add more options as needed */}
                                </select>
                            </div>
                            <div className="col">
                                <label className="form-label">Department :</label>
                                <select className="form-select" value={selectedDropdown2} onChange={(e) => setSelectedDropdown2(e.target.value)}>
                                    <option value="">Select Option</option>
                                    <option value="OptionA">Option A</option>
                                    <option value="OptionB">Option B</option>
                                    {/* Add more options as needed */}
                                </select>
                            </div>
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Location :</label>
                            <select className="form-select" value={selectedDropdown1} onChange={(e) => setSelectedDropdown1(e.target.value)}>
                                <option value="">Select Option</option>
                                <option value="Option1">Option 1</option>
                                <option value="Option2">Option 2</option>
                                {/* Add more options as needed */}
                            </select>
                        </div>
                        {/* ... (continue with other form elements) */}
                        <div className="col">
                            <label className="form-label">Start Date:</label>
                            <input type="date" className="form-control" value={startDate} onChange={handleStartDateChange} />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">End Date:</label>
                            <input type="date" className="form-control" value={endDate} onChange={handleEndDateChange} />
                        </div>
                        {/* ... (continue with other form elements) */}
                        <div className="mb-3 font-italic">
                            <label className="form-label ">Status:</label>
                            <p className="form-text  fw-bolder">Registered</p>
                        </div>
                        <button type="submit" className="btn btn-primary">Submit</button>
                        {error && <p className="text-danger mt-3">{error}</p>}
                    </form>
                </div>
            </div>
        </div>

    );
};

export default ProjectForm;
