import React, { useState } from 'react';

const ProjectForm = () => {
  const [projectName, setProjectName] = useState('');
  const [selectedDropdown1, setSelectedDropdown1] = useState('');
  const [selectedDropdown2, setSelectedDropdown2] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [error, setError] = useState('');

  const handleStartDateChange = (e) => {
    setStartDate(e.target.value);
    if (endDate && e.target.value > endDate) {
      setEndDate(''); // Reset end date if it's smaller than the new start date
    }
  };

  const handleEndDateChange = (e) => {
    setEndDate(e.target.value);
    if (startDate && e.target.value < startDate) {
      setError('End date cannot be smaller than start date');
    } else {
      setError('');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate all fields before submitting
    if (!projectName || !selectedDropdown1 || !selectedDropdown2 || !startDate || !endDate) {
      setError('Please fill in all fields');
      return;
    }

    // Perform the project insertion logic here
    console.log('Project details submitted:', {
      projectName,
      selectedDropdown1,
      selectedDropdown2,
      startDate,
      endDate,
      status: 'Registered',
    });

    // Clear form and reset state
    setProjectName('');
    setSelectedDropdown1('');
    setSelectedDropdown2('');
    setStartDate('');
    setEndDate('');
    setError('');
  };

  return (
    <div>
      <h1>Insert Project Record</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Project Name:</label>
          <input type="text" value={projectName} onChange={(e) => setProjectName(e.target.value)} />
        </div>
        <div>
          <label>Dropdown 1:</label>
          {/* Replace the options with your actual values */}
          <select value={selectedDropdown1} onChange={(e) => setSelectedDropdown1(e.target.value)}>
            <option value="">Select Option</option>
            <option value="Option1">Option 1</option>
            <option value="Option2">Option 2</option>
            {/* Add more options as needed */}
          </select>
        </div>
        <div>
          <label>Dropdown 2:</label>
          {/* Replace the options with your actual values */}
          <select value={selectedDropdown2} onChange={(e) => setSelectedDropdown2(e.target.value)}>
            <option value="">Select Option</option>
            <option value="OptionA">Option A</option>
            <option value="OptionB">Option B</option>
            {/* Add more options as needed */}
          </select>
        </div>
        <div>
          <label>Start Date:</label>
          <input type="date" value={startDate} onChange={handleStartDateChange} />
        </div>
        <div>
          <label>End Date:</label>
          <input type="date" value={endDate} onChange={handleEndDateChange} />
        </div>
        <div>
          <p>Status: Registered</p>
        </div>
        <button type="submit">Submit</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
    </div>
  );
};

export default ProjectForm;
