import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Invalid email format');
      return;
    }

    // Validate non-empty fields
    if (!email || !password) {
      setError('Email and password are required');
      return;
    }

    // Fetch user details from the database (replace this with your actual implementation)
    const isValidUser = validateUserFromDatabase(email, password);

    if (isValidUser) {
      // Navigate to the dashboard
      navigate('/dashboard');
    } else {
      setError('Invalid user');
    }
  };

  const validateUserFromDatabase = (email, password) => {
    // Replace this with your actual logic to validate user details from the database
    // For simplicity, returning true for any email and password combination
    return true;
  };

  return (
    <div>
      <h1>Login Page</h1>
      <div>
        <label>Email:</label>
        <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <div>
        <label>Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      <button onClick={handleLogin}>Login</button>
      {error && <p>{error}</p>}
    </div>
  );
};

export default Login;
