import { useEffect, useState } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';


const DashboardChart = () => {
  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    // Fetch data from your API
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3001/dashboard/chart');
        const data = await response.json();

        // Process data and update chartOptions
        const processedData = processData(data);
        setChartOptions(processedData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const processData = (data) => {
    // Process data to match Highcharts series format
    // Example structure: [{ name: 'Strategy', data: [14, 19] }, { name: 'Finance', data: [6, 7] }]
    const processedData = data.map((department) => ({
      name: department.name,
      data: [department.closedProjects, department.totalProjects],
    }));

    return {
      chart: {
        type: 'column',
      },
      title: {
        text: 'Department-wise Success Percentage',
      },
      xAxis: {
        categories: ['Closed Projects', 'Total Projects'],
      },
      yAxis: {
        title: {
          text: 'Percentage',
        },
      },
      series: processedData,
    };
  };



  return <HighchartsReact highcharts={Highcharts} options={chartOptions} />;
};

export default DashboardChart;
