import React, { useEffect, useState } from 'react';


const ProjectList = () => {
  const [projects, setProjects] = useState([]);
  const [filteredProjects, setFilteredProjects] = useState([]);
  const [searchText, setSearchText] = useState('');
  const [sortColumn, setSortColumn] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const [currentPage, setCurrentPage] = useState(1);
  const projectsPerPage = 10;

  // Fetch project data from the database
  const getProjects = async () => {
    try {
      // Assuming you have an API endpoint to fetch projects
      const response = await fetch('api/projects'); // Update with your actual API endpoint
      const data = await response.json();
      setProjects(data);
      setFilteredProjects(data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  // Load projects on component mount
  useEffect(() => {
    getProjects();
  }, []);

  // Update status of a project
  const updateStatus = async (projectId, newStatus) => {
    try {
      // Assuming you have an API endpoint to update project status
      await fetch(`api/projects/${projectId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status: newStatus }),
      });

      // Update the status in the local state
      const updatedProjects = projects.map((project) =>
        project.id === projectId ? { ...project, status: newStatus } : project
      );

      setProjects(updatedProjects);
      setFilteredProjects(updatedProjects);
    } catch (error) {
      console.error('Error updating project status:', error);
    }
  };

  // Handle status update button click
  const handleStatusUpdate = (projectId, newStatus) => {
    updateStatus(projectId, newStatus);
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    const searchText = e.target.value;
    setSearchText(searchText);

    // Filter projects based on search text
    const filtered = projects.filter((project) =>
      Object.values(project).some(
        (value) => typeof value === 'string' && value.toLowerCase().includes(searchText.toLowerCase())
      )
    );

    setFilteredProjects(filtered);
  };

  // Handle sorting
  const handleSortChange = (column) => {
    if (column === sortColumn) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortOrder('asc');
    }
  };

  // Perform sorting based on the selected column and order
  const sortedProjects = [...filteredProjects].sort((a, b) => {
    const aValue = a[sortColumn];
    const bValue = b[sortColumn];

    if (sortOrder === 'asc') {
      return aValue.localeCompare(bValue, undefined, { numeric: true, sensitivity: 'base' });
    } else {
      return bValue.localeCompare(aValue, undefined, { numeric: true, sensitivity: 'base' });
    }
  });

  // Calculate pagination
  const indexOfLastProject = currentPage * projectsPerPage;
  const indexOfFirstProject = indexOfLastProject - projectsPerPage;
  const currentProjects = sortedProjects.slice(indexOfFirstProject, indexOfLastProject);

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div> <Navbarleft />
      <div>
        <h1>Project Listing</h1>
        <input type="text" placeholder="Search..." value={searchText} onChange={handleSearchChange} />

        <table className="table">
          {/* ... (Table headers) */}
          <thead>
            <tr>
              <th onClick={() => handleSortChange('projectName')}>Project Name</th>
              {/* ... (Other columns) */}
              <th onClick={() => handleSortChange('status')}>Status</th>
            </tr>
          </thead>
          <tbody>
            {currentProjects.map((project) => (
              <tr key={project.id}>
                {/* ... (Table data) */}
                <td>{project.projectName}</td>
                {/* ... (Other columns) */}
                <td>{project.status}</td>
                <td>
                  <button onClick={() => handleStatusUpdate(project.id, 'Running')}>Start</button>
                  <button onClick={() => handleStatusUpdate(project.id, 'Closed')}>Close</button>
                  <button onClick={() => handleStatusUpdate(project.id, 'Cancelled')}>Cancel</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Pagination */}
        <ul className="pagination">
          {Array.from({ length: Math.ceil(filteredProjects.length / projectsPerPage) }).map((_, index) => (
            <li key={index} className={`page-item ${currentPage === index + 1 ? 'active' : ''}`}>
              <button onClick={() => paginate(index + 1)} className="page-link">
                {index + 1}
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>

  );
};

export default ProjectList;
