import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async  () => {
    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Invalid email format');
      return;
    }

    // Validate non-empty fields
    if (!email || !password) {
      setError('Email and password are required');
      return;
    }

    try {
      const isValidUser = await validateUserFromDatabase(email, password);
      
      if (isValidUser) {
        navigate('/Home');
      } else {
        setError('Invalid user');
      }
    } catch (error) {
      console.error('Error validating user:', error);
      setError('Failed to validate user. Please try again.');
    }
  };

  const validateUserFromDatabase = async  (email, password) => {
    try {
      const response = await fetch('http://localhost:3001/user/authenticate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error('Failed to validate user');
      }

      const result = await response.json();
      return result.isValid; // Adjust based on your API response structure
    } catch (error) {
      throw error;
    }
  };

  return (
    
    <div className="container-fluid">
    <div className="row d-flex justify-content-center align-items-center h-100">
      <div className="col-12">

        <div className="card bg-white my-5 mx-auto" style={{ borderradius: "1rem;", maxwidth: "500px"}}>
          <div className="card-body p-5 w-100 d-flex flex-column">

            <h2 className="fw-bold mb-2 text-center">Login To Get Start</h2>
            <p className="text-white-50 mb-3">Please enter your login and password!</p>

            <div className="form-group mb-4 w-100">
              <label for="formControlLg">Email address</label>
              <input className="form-control" type="email" id="formControlLg" size="lg" value={email} onChange={(e) => setEmail(e.target.value)}/>
            </div>

            <div className="form-group mb-4 w-100">
              <label for="formControlLg">Password</label>
              <input className="form-control" type="password" id="formControlLg" size="lg" value={password} onChange={(e) => setPassword(e.target.value)}/>
            </div>

            <button className="btn btn-lg btn-primary w-100" onClick={handleLogin}>
              Login
            </button>
            {error && <p className="error-message">{error}</p>}

            <hr className="my-4"/>

          </div>
        </div>

      </div>
    </div>
  </div>
  );
};

export default Login;
