import React, { useState } from 'react';

const ProjectForm = () => {
    const [projectName, setProjectName] = useState('');
    const [selectedDropdown1, setSelectedDropdown1] = useState('');
    const [selectedDropdown2, setSelectedDropdown2] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [error, setError] = useState('');

    const handleStartDateChange = (e) => {
        setStartDate(e.target.value);
        if (endDate && e.target.value > endDate) {
            setEndDate(''); // Reset end date if it's smaller than the new start date
        }
    };

    const handleEndDateChange = (e) => {
        setEndDate(e.target.value);
        if (startDate && e.target.value < startDate) {
            setError('End date cannot be smaller than start date');
        } else {
            setError('');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Validate all fields before submitting
        if (!projectName || !selectedDropdown1 || !selectedDropdown2 || !startDate || !endDate) {
            setError('Please fill in all fields');
            return;
        }

        // Perform the project insertion logic here
        console.log('Project details submitted:', {
            projectName,
            selectedDropdown1,
            selectedDropdown2,
            startDate,
            endDate,
            status: 'Registered',
        });

        // Clear form and reset state
        setProjectName('');
        setSelectedDropdown1('');
        setSelectedDropdown2('');
        setStartDate('');
        setEndDate('');
        setError('');
    };

    return (
        <div className="container mt-5">
  <div className="card m-1" style={{ width: '45rem', maxHeight: '1200px' }}>
    <div className="card-body">
      <h1 className="card-title">Insert Project Record</h1>
      <form onSubmit={handleSubmit} className="needs-validation" noValidate>
        <div className="mb-3">
          <label className="form-label">Project Name:</label>
          <input
            type="text"
            placeholder="Enter project theme"
            className="form-control is-valid"
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            
          />
          
        </div>
        {/* ... (continue with other form elements in a similar manner) */}
        <div className="mb-3">
          <label className="form-label">Start Date:</label>
          <input type="date" className="form-control is-invalid" value={startDate} onChange={handleStartDateChange} required />
          <div className="invalid-feedback">Please provide a valid start date.</div>
        </div>
        <div className="mb-3">
          <label className="form-label">End Date:</label>
          <input type="date" className="form-control is-invalid" value={endDate} onChange={handleEndDateChange} required />
          <div className="invalid-feedback">Please provide a valid end date.</div>
        </div>
        {/* ... (continue with other form elements) */}
        <div className="form-group">
          <div className="form-check">
            <input
              className="form-check-input is-invalid"
              type="checkbox"
              value=""
              id="invalidCheck3"
              required
            />
            <label className="form-check-label" htmlFor="invalidCheck3">
              Agree to terms and conditions
            </label>
            <div className="invalid-feedback">You must agree before submitting.</div>
          </div>
        </div>
        <button type="submit" className="btn btn-primary">
          Submit form
        </button>
        {error && <p className="text-danger mt-3">{error}</p>}
      </form>
    </div>
  </div>
</div>


    );
};

export default ProjectForm;
