import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Invalid email format');
      return;
    }

    // Validate non-empty fields
    if (!email || !password) {
      setError('Email and password are required');
      return;
    }

    // Fetch user details from the database (replace this with your actual implementation)
    const isValidUser = validateUserFromDatabase(email, password);

    if (isValidUser) {
      // Navigate to the dashboard
      navigate('/dashboard');
    } else {
      setError('Invalid user');
    }
  };

  const validateUserFromDatabase = (email, password) => {
    // Replace this with your actual logic to validate user details from the database
    // For simplicity, returning true for any email and password combination
    return true;
  };

  return (
    
    <MDBContainer fluid>

    <MDBRow className='d-flex justify-content-center align-items-center h-100'>
      <MDBCol col='12'>

        <MDBCard className='bg-white my-5 mx-auto' style={{borderRadius: '1rem', maxWidth: '500px'}}>
          <MDBCardBody className='p-5 w-100 d-flex flex-column'>

            <h2 className="fw-bold mb-2 text-center">Sign in</h2>
            <p className="text-white-50 mb-3">Please enter your login and password!</p>

            <MDBInput wrapperClass='mb-4 w-100' label='Email address' id='formControlLg' type='email' size="lg"/>
            <MDBInput wrapperClass='mb-4 w-100' label='Password' id='formControlLg' type='password' size="lg"/>

            <MDBCheckbox name='flexCheck' id='flexCheckDefault' className='mb-4' label='Remember password' />

            <MDBBtn size='lg'>
              Login
            </MDBBtn>

            <hr className="my-4" />

            <MDBBtn className="mb-2 w-100" size="lg" style={{backgroundColor: '#dd4b39'}}>
              <MDBIcon fab icon="google" className="mx-2"/>
              Sign in with google
            </MDBBtn>

            <MDBBtn className="mb-4 w-100" size="lg" style={{backgroundColor: '#3b5998'}}>
              <MDBIcon fab icon="facebook-f" className="mx-2"/>
              Sign in with facebook
            </MDBBtn>

          </MDBCardBody>
        </MDBCard>

      </MDBCol>
    </MDBRow>

  </MDBContainer>
  );
};

export default Login;
